    stopDrawing() {
      if (this.isdraw) {
        this.isdraw = false;
        console.log("xxxxx");
        if (this.circ) {
          this.shapeType = 'Circle';
          this.circles.push({
            ...this.currentShape
          });
          this.shapes.push({
            ...this.currentShape
          });
        } else if (this.rectangle) {
          this.shapeType = 'Rectangle';
          this.rectangles.push({
            ...this.currentShape
          });
          this.shapes.push({
            ...this.currentShape
          });
        } else if (this.ellips) {
          this.shapeType = 'Ellipse';
          this.ellipses.push({
            ...this.currentShape
          });
          this.shapes.push({
            ...this.currentShape
          });
        } else if (this.lin || this.bru) {
          this.shapeType = 'Line';
          this.lines.push({
            ...this.currentShape
          });
          this.shapes.push({
            ...this.currentShape
          });
        } else if (this.sqrt) {
          this.shapeType = 'Square';
          this.squares.push({
            ...this.currentShape
          });
          this.shapes.push({
            ...this.currentShape
          });
        } else if (this.tria) {
          this.shapeType = 'Triangle';
          this.triangles.push({
            ...this.currentShape
          });
          this.shapes.push({
            ...this.currentShape
          });
        }
        this.createShape();
        this.currentShape = null;
        this.circ = false;
        this.ellips = false;
        this.rectangle = false;
        this.lin = false;
        this.sqrt = false;
        this.tria = false;
        this.mov = false;
        this.bru = false;
