    draw() {
      if (this.rectangle) {
        if (this.isdraw) {
          const stage = this.$refs.stage.getStage();
          if (stage) {
            const position = stage.getPointerPosition();
            if (position) {
              this.currentShape.width = position.x - this.currentShape.x;
              this.currentShape.height = position.y - this.currentShape.y;
            }
          }
        }
      }
      //--------------------------------------------------------------------------------
      else if (this.circ) {
        if (this.isdraw) {
          const stage = this.$refs.stage.getStage();
          if (stage) {
            const position = stage.getPointerPosition();
            if (position) {
              this.currentShape.radius = Math.sqrt(Math.pow(position.x - this.currentShape.x, 2) + Math.pow(position.y - this.currentShape.y, 2));
            }
          }
        }
      }
      //************************************************************************************************** */
      else if (this.ellips) {
