          const stage = this.$refs.stage.getStage();
          if (stage) {
            const position = stage.getPointerPosition();
            if (position) {
              this.currentShape.points[2] = position.x;
              this.currentShape.points[3] = position.y;
            }
          }
        }
      }
      //******************************************** */
      else if (this.sqrt) {
        if (this.isdraw) {
          const stage = this.$refs.stage.getStage();
          if (stage) {
            const position = stage.getPointerPosition();
            if (position) {
              this.currentShape.width = position.x - this.currentShape.x;
              this.currentShape.height = position.x - this.currentShape.x;
            }
          }
        }
      }
      //************************************ **/
      else if (this.tria) {
        if (this.isdraw) {
          const stage = this.$refs.stage.getStage();
          if (stage) {
            const position = stage.getPointerPosition();
            if (position) {
              this.currentShape.radius = Math.sqrt(Math.pow(position.x - this.currentShape.x, 2) + Math.pow(position.y - this.currentShape.y, 2));
            }
          }
        }
      }
